import React from 'react';
import WelcomeMessage from './components/welcomeMessage';
import './App.css';

class App extends  React.Component{
  constructor(){
    super();
    this.state ={
      showWelcome: true,
      showConsentModal: false,
      userConsent: '',
    }
    this.closeWelcome = this.closeWelcome.bind(this);
  }
  closeWelcome ()  {
    this.setState({showWelcome : false, showConsentModal: true}) ;
  };

  componentDidMount(){	
	}

  renderConsentModal() {
    if (!this.state.showConsentModal) return null;

    const handleConsent = (consentType) => {
      this.setState({ showConsentModal: false, userConsent: consentType });
    };

    return (
      <div className="modal">
        <button onClick={() => handleConsent('Full consent')}>Full consent</button>
        <button onClick={() => handleConsent('Semi-consent')}>Semi-consent</button>
        <button onClick={() => handleConsent('No consent')}>No consent</button>
      </div>
    );
  }

  render (){
    return(
      <div>
      {this.state.showWelcome ? (
        <WelcomeMessage onClose={this.closeWelcome} />
      ) : this.state.showConsentModal ? (
        this.renderConsentModal() // This will render the modal
      ) : (
        <div className="background-image">
          {/* Background image displayed, proceed with app content */}
        </div>
      )}
    </div>
    );
  }
}

export default App;
