import React, { useEffect } from 'react';

const WelcomeMessage = ({ duration = 2000, onClose }) => {
  useEffect(() => {
    // Close the welcome message after 'duration' milliseconds
    const timer = setTimeout(onClose, duration);
    return () => clearTimeout(timer);
  }, [onClose, duration]);

  return (
    <div style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, display: 'flex', justifyContent: 'center', alignItems: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
      <div style={{ padding: '20px', backgroundColor: '#fff', borderRadius: '5px' }}>
        Welcome to Ford
      </div>
    </div>
  );
};

export default WelcomeMessage;
