import React, { Component } from 'react';
import "./F2F.css";
import Messaging from './Messaging';

class F2F extends Component {
  componentDidMount() {
    Messaging.connectWithPromise()
      .then(() => {
        console.log('Connected to Solace Cloud');
      })
      .catch((error) => {
        console.error('Failed to connect to Solace Cloud', error);
      });

    // Adjust animation duration based on car speed for the initial dot
    this.adjustDotSpeed(100, 'dot'); // Adjust car speed as needed in km/hr
  }

  adjustDotSpeed(carSpeedKmPerHour, dotId) {
    const dotElement = document.getElementById(dotId);
    if (!dotElement) return; // Exit if dotElement is not found

    const containerWidth = document.querySelector('.container').clientWidth;
    const dotWidth = dotElement.offsetWidth;
    const distanceToTravel = containerWidth - dotWidth; // Distance to travel

    // Calculate time taken to travel the distance at given speed (in milliseconds)
    const timeTakenInMillis =
      (distanceToTravel / 1000 / (carSpeedKmPerHour / 3600)) * 1000;

    // Update animation duration
    dotElement.style.animationDuration = `${timeTakenInMillis}ms`;
  }

  renderSecondDot() {
    const { initialPosition } = this.props;

    // Render nothing if no initialPosition is provided
    if (!initialPosition) return null;

    // Return a div for the second dot with dynamic style based on initialPosition
    return (
      <div
        className="dot"
        id="dot2"
        style={{
          left: `${initialPosition.x}px`,
          animationPlayState: 'running', // Start the animation
        }}
      />
    );
  }

  render() {
    return (
      <div className="container">
        <div className="line"></div>
        <div className="dot" id="dot"></div>
        {this.renderSecondDot()}
      </div>
    );
  }
}

export default F2F;
