import React, { Component } from 'react';
import './NewsTicker.css'; // You'll need to create this CSS file for styling

class NewsTicker extends Component {
  state = {
    newsItems: [
      'News 1',
      'News 2',
      'News 3',
      'News 4',
      'News 5',
      // Add more news items as needed
    ],
    currentIndex: 0,
    position: null, // State to hold position
  };

  componentDidMount() {
    // Start the ticker
    this.startTicker();
  }

  componentWillUnmount() {
    // Stop the ticker when the component is unmounted
    clearInterval(this.tickerInterval);
  }

  startTicker = () => {
    // Set up a timer to move the ticker
    this.tickerInterval = setInterval(() => {
      // Update the current index
      this.setState((prevState) => ({
        currentIndex:
          prevState.currentIndex === prevState.newsItems.length - 1
            ? 0
            : prevState.currentIndex + 1,
      }));
    }, 2000); // Adjust the interval as needed (in milliseconds)
  };

  // Update state with new position when received via props
  componentDidUpdate(prevProps) {
    if (prevProps.position !== this.props.position) {
      this.setState({ position: this.props.position });
    }
  }

  publishDotPosition = () => {
    // Get dot position
    const dotElement = document.getElementById('dot');
    const position = {
      x: position.x,
      y: position.y,
      car_no:"CAR-01"
    };

    const payload = JSON.stringify(position)
    // Publish dot position to a topic named 'car'
    Messaging.publish('cars', payload);

    // Pass position to parent component
    this.props.updatePosition(position);
  };

  render() {
    const { newsItems, currentIndex, position } = this.state;

    return (
      <div className="news-ticker-container">
        <div className="news-ticker">
          {newsItems.map((item, index) => (
            <div
              key={index}
              className={
                index === currentIndex ? 'news-item active' : 'news-item'
              }
            >
              {item}
            </div>
          ))}
        </div>
        {position && ( // Render position if available
          <div className="position-display">
            Position: {position.x}, {position.y} - Car: {position.car_no}
          </div>
        )}
      </div>
    );
  }
}

export default NewsTicker;
