import React from 'react';
import WelcomeMessage from './components/welcomeMessage';
import mySound from './sounds/ford-start.mp3'
import './App.css';

class App extends  React.Component{
  constructor(){
    super();
    this.state ={
      showWelcome: true,
      showBackground: true,
      showConsentModal: false,
      userConsent: '',
    }
    this.closeWelcome = this.closeWelcome.bind(this);
    this.audio = new Audio(mySound);
  }
  closeWelcome ()  {
    this.setState({showWelcome : false, showConsentModal: true}) ;
  };

  componentDidMount(){	
    this.audio.play().catch(error => console.error("Audio play failed:", error));

    // Remove background and stop sound after 3 seconds
    setTimeout(() => {
      this.setState({ showBackground: false });
      this.audio.pause(); // Optionally reset the sound if needed
      this.audio.currentTime = 0; // Reset time to start
    }, 3000);
	}

  renderConsentModal() {
    if (!this.state.showConsentModal) return null;

    const handleConsent = (consentType) => {
      this.setState({ showConsentModal: false, userConsent: consentType });
    };

    return (
      <div className="modal">
      <div className="modal-content">
        <h2>Which service would you like to choose from</h2>
        <button onClick={() => handleConsent('Charging_points')}>Charging points</button>
        <button onClick={() => handleConsent('Connect')}>Connect with fellow Ford travelers</button>
        <button onClick={() => handleConsent('Exit')}>Exit</button>
      </div>
    </div>
    );
  }

  render (){
    return(
      <div>
      {this.state.showWelcome ? (
        <WelcomeMessage onClose={this.closeWelcome} />
      ) : this.state.showConsentModal ? (
        this.renderConsentModal() 
      ) : (
        <div className="background-image">
          {/* Background image displayed*/}
        </div>
      )}
    </div>
    );
  }
}

export default App;
