import React from 'react';
import WelcomeMessage from './components/welcomeMessage';
import './App.css';

class App extends  React.Component{
  constructor(){
    super();
    this.state ={
      showWelcome: true,
    }
  }
  closeWelcome ()  {
    this.setState({showWelcome : false}) ;
  };

  componentDidMount(){

	}
  render (){
    return(
      <div className="welcome-message-container">
      {this.state.showWelcome && <WelcomeMessage onClose={this.closeWelcome()} />}
      </div>
    );
  }
}

export default App;
